package com.array;

public class Array_DefaultValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
// default values asigned
byte[] b=new byte[5];
short[] c=new short[5];
int[] a=new int[5];
long[] d=new long[5];
float[] e=new float[5];
double[] f=new double[5];
boolean[] g=new boolean[5];
char[] h=new char[5];

System.out.println("byte "+b[2]);
System.out.println("short "+c[2]);
System.out.println("int "+a[2]);
System.out.println("long "+d[2]);
System.out.println("float "+e[2]);
System.out.println("double "+f[2]);
System.out.println("boolean "+g[2]);
System.out.println("char "+h[2]);

	}

}
